mapper.select:
selectfrom TFS_FILE_INFO
         and FILE_CREATOR_ID like #{fileCreatorId} 
         and FILE_CREATOR_NAME like #{fileCreatorName} 
         and FILE_NAME like #{fileName} 
         and FILE_STORE_TYPE like #{fileStoreType} 
         and FILE_STORE_PATH like #{fileStorePath} 
         and FILE_STORE_NAME like #{fileStoreName} 
         and FILE_ORGAN_ID like #{fileOrganId} 
         and FILE_ORGAN_NAME like #{fileOrganName} 
         and FILE_EXTENSION like #{fileExtension} 
         and FILE_DESCRIPTION like #{fileDescription} 
         and FILE_STATUS like #{fileStatus} 
         and FILE_CATEGORY like #{fileCategory} 
         and FILE_HASH_CODE like #{fileHashCode} 
         and FILE_GROUP_ID like #{fileGroupId} 
         and FILE_CODE like #{fileCode} 
         and FILE_CAPTION like #{fileCaption} 
    ORDER by FILE_ORDER_NUM DESC
